mapper.select:
		SELECT  FROM asset_deal WHERE _DB_STATE = 1
		AND _ID = #{params.id}

        AND TRADE_ID = #{params.tradeId}
        AND USER_ID = #{params.userId}
        AND PAY_ASSET_ID = #{params.payAssetId}
        AND PAY_ASSET_AMOUNT = #{params.payAssetAmount}
        AND RETURN_ASSET_ID = #{params.returnAssetId}
        AND RETURN_ASSET_AMOUNT = #{params.returnAssetAmount}
        AND PAY_FEE = #{params.payFee}


            OR ( 1 != 1
              OR USER_ID LIKE CONCAT('%',#{params.keywords},'%')
              OR PAY_ASSET_AMOUNT LIKE CONCAT('%',#{params.keywords},'%')
              OR RETURN_ASSET_AMOUNT LIKE CONCAT('%',#{params.keywords},'%')
              OR PAY_FEE LIKE CONCAT('%',#{params.keywords},'%')
            )
