mapper.select:
		SELECT  FROM asset_flow WHERE _DB_STATE = 1
		AND _ID = #{params.id}

        AND USER_ID = #{params.userId}
        AND REF_ID = #{params.refId}
        AND TYPE = #{params.type}
        AND TYPE_TINY = #{params.typeTiny}
        AND ASSET_ID = #{params.assetId}
        AND ASSET_AMOUNT = #{params.assetAmount}
        AND REF_UPDATE = #{params.refUpdate}


            OR ( 1 != 1
              OR USER_ID LIKE CONCAT('%',#{params.keywords},'%')
              OR TYPE LIKE CONCAT('%',#{params.keywords},'%')
              OR TYPE_TINY LIKE CONCAT('%',#{params.keywords},'%')
              OR ASSET_AMOUNT LIKE CONCAT('%',#{params.keywords},'%')
              OR REF_UPDATE LIKE CONCAT('%',#{params.keywords},'%')
            )
