mapper.select:
		SELECT  FROM tb_cms_discuss WHERE _DB_STATE = 1
		AND _ID = #{params.id}

        AND TYPE = #{params.type}
        AND TITLE = #{params.title}
        AND CONTENT = #{params.content}
        AND COURSE_ID = #{params.courseId}
        AND USER_ID = #{params.userId}
        AND DISCUSS_ID = #{params.discussId}
        AND REPLY_DISCUSS_ID = #{params.replyDiscussId}
        AND REF_DISCUSS_ID = #{params.refDiscussId}
        AND ANSWER_COUNT = #{params.answerCount}


            OR ( 1 != 1
              OR TITLE LIKE CONCAT('%',#{params.keywords},'%')
              OR CONTENT LIKE CONCAT('%',#{params.keywords},'%')
            )
