mapper.select:
		SELECT  FROM tb_system_permission WHERE _DB_STATE = 1
		AND _ID = #{params.id}

        AND P_ID = #{params.pId}
        AND NAME = #{params.name}
        AND TYPE = #{params.type}
        AND VAL = #{params.value}
        AND PATH = #{params.path}
        AND STATE = #{params.state}
        AND LEVEL = #{params.level}


            OR ( 1 != 1
              OR NAME LIKE CONCAT('%',#{params.keywords},'%')
              OR VAL LIKE CONCAT('%',#{params.keywords},'%')
              OR PATH LIKE CONCAT('%',#{params.keywords},'%')
            )
